mapper.select:
SELECT B.* FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT CMNT_NO, B.CODE_NAME AS CMNT_CODE, CMNT_NAME, CMNT_SIZE, CMNT_CAPA, CMNT_LIMIT, SUBSTR(CMNT_OPEN,1,2)||':'||SUBSTR(CMNT_OPEN,3,4) AS CMNT_OPEN, SUBSTR(CMNT_CLOSE,1,2)||':'||SUBSTR(CMNT_CLOSE,3,4) AS CMNT_CLOSE, CMNT_DESC, APT_CODE FROM COMMUNITY A LEFT OUTER JOIN COMMONCODE B ON (A.CMNT_CODE = B.CODE_ID)
ORDER BY CMNT_NO DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.insert:

	SELECT MAX(CMNT_NO)+1
	FROM COMMUNITY


INSERT INTO community (
    CMNT_NO,
    CMNT_CODE,
    CMNT_NAME,
    CMNT_SIZE,
    CMNT_CAPA,
    CMNT_LIMIT,
    CMNT_OPEN,
    CMNT_CLOSE,
    CMNT_DESC,
    APT_CODE
) VALUES (
   #{cmntNo, jdbcType=NUMERIC}
	, #{cmntCode, jdbcType=VARCHAR}
	, #{cmntName, jdbcType=VARCHAR}
	, #{cmntSize, jdbcType=NUMERIC}
	, #{cmntCapa, jdbcType=NUMERIC}
	, #{cmntLimit, jdbcType=NUMERIC}
	, #{cmntOpen, jdbcType=VARCHAR}
	, #{cmntClose, jdbcType=VARCHAR}
	, #{cmntDesc, jdbcType=CLOB}
	, #{aptCode, jdbcType=VARCHAR}
)

mapper.update:
UPDATE COMMUNITY
    SET
		CMNT_CODE = #{cmntCode}
		, CMNT_NAME = #{cmntName}
		, CMNT_SIZE = #{cmntSize}
		, CMNT_CAPA = #{cmntCapa}
		, CMNT_LIMIT = #{cmntLimit}
		, CMNT_OPEN = #{cmntOpen}
		, CMNT_CLOSE = #{cmntClose}
		, CMNT_DESC = #{cmntDesc}
WHERE
    CMNT_NO = #{cmntNo} 
    AND   APT_CODE = #{aptCode}
