mapper.select:
SELECT B.* FROM ( SELECT A.*, ROWNUM SVCRUM FROM ( SELECT SERVICE.SVC_ID , SERVICE.APT_CODE , COMMONCODE.CODE_NAME SVC_CODE, SVC_NAME, SVC_DEL, TO_CHAR(SERVICECONTRACT.SVC_START, 'YYYY-MM-DD')SVC_START, TO_CHAR(SERVICECONTRACT.SVC_END, 'YYYY-MM-DD')SVC_END, TO_CHAR(SERVICECONTRACT.SVC_DEPOSIT,'999,999,999,999')SVC_DEPOSIT FROM SERVICE LEFT JOIN SERVICECONTRACT ON SERVICE.SVC_ID = SERVICECONTRACT.SVC_ID LEFT JOIN COMMONCODE ON SERVICE.SVC_CODE = COMMONCODE.CODE_ID
ORDER BY SVC_DEL,SVC_ID DESC ) A ) B
WHERE SVCRUM >= #{startRow} AND SVCRUM <= #{endRow}

SELECT COUNT(*)
FROM SERVICEFILE
WHERE SVC_ID =#{svcId}

mapper.update:
UPDATE SERVICE
SET
SVC_DEL=#{svcDel}
WHERE SVC_ID = #{svcId}

	UPDATE SERVICECONTRACT
    SET
    SVC_START =#{svcStart},
    SVC_END =#{svcEnd},
    SVC_DEPOSIT=#{svcDeposit}
    WHERE SVC_ID = #{svcId}
