mapper.update:
UPDATE BOARD
SET
   BO_TITLE = #{boTitle,jdbcType=VARCHAR}
  ,BO_CONTENT = #{boContent,jdbcType=CLOB}
  ,BO_DATE = SYSDATE
WHERE BO_NO = #{boNo}

UPDATE BOARD
   SET BO_HIT = BO_HIT + 1
 WHERE BO_NO = #{boNo}

mapper.select:
SELECT BO_NO
  FROM BOARD 
 WHERE BO_PARENT = #{boNo}

WITH BOARDTB AS( SELECT BO_NO ,BO_PARENT ,BO_DEPTH FROM BOARD WHERE BO_TYPE='OQNA' )
SELECT B.* FROM ( SELECT ROWNUM RNUM, A.* FROM( SELECT A.BO_NO ,BO_TITLE ,B.MEM_NICK AS "BO_WRITER" ,TO_CHAR(BO_DATE, 'YYYY-MM-DD') BO_DATE ,BO_HIT FROM BOARD A LEFT JOIN MEMBER B ON (A.BO_WRITER=B.MEM_ID) LEFT JOIN BOARDTB C ON (A.BO_NO = C.BO_PARENT)
AND C.BO_PARENT IS NULL AND BO_DELETE = 'N' AND A.BO_DEPTH = '1' )A )B WHERE RNUM BETWEEN #{startRow} AND #{endRow}
